"""
Error code registry for CIDX server.

Provides centralized error code definitions for consistent logging and monitoring.
Each error code follows the format: {SUBSYSTEM}-{CATEGORY}-{NUMBER}
"""

import re
from dataclasses import dataclass
from enum import Enum
from typing import Dict, Optional


class Severity(Enum):
    """Log severity levels."""
    WARNING = "warning"
    ERROR = "error"
    CRITICAL = "critical"


@dataclass(frozen=True)
class ErrorDefinition:
    """
    Definition of an error code.

    Attributes:
        code: Unique error code in format {SUBSYSTEM}-{CATEGORY}-{NUMBER}
        description: Human-readable description of the error
        severity: Log severity level (WARNING, ERROR, CRITICAL)
        action: Suggested action or resolution steps
    """
    code: str
    description: str
    severity: Severity
    action: str


# Subsystem prefixes for error codes
SUBSYSTEMS = {
    "AUTH",      # Authentication and authorization
    "GIT",       # Git operations
    "MCP",       # MCP protocol and tools
    "CACHE",     # Caching operations
    "REPO",      # Repository management
    "QUERY",     # Query operations
    "VALID",     # Validation
    "DEPLOY",    # Deployment
    "SCIP",      # SCIP code intelligence
    "TELEM",     # Telemetry
    "STORE",     # Storage operations
    "SVC",       # Service operations
    "WEB",       # Web routes and handlers
    "APP",       # Application lifecycle
    "MONITOR"    # Self-monitoring (Story #72)
}


