def validate_error_code_format(code: str) -> bool:
    """Validate error code format: SUBSYSTEM-CATEGORY-NUMBER."""
    if not code:
        return False
    pattern = r'^[A-Z]{2,6}-[A-Z]{2,8}-\d{3}$'
    return bool(re.match(pattern, code))


def get_error_definition(code: str) -> Optional[ErrorDefinition]:
    """Look up an error definition by code."""
    return ERROR_REGISTRY.get(code)
